mapper.select:
SELECT id, content, writer, created_date, modified_date, post_id, reply_target_id
FROM vip_comment
WHERE id=#{id};

SELECT id, content, writer, created_date, modified_date, post_id, reply_target_id
FROM vip_comment
WHERE post_id = #{postId};

mapper.update:
UPDATE vip_comment
SET content = #{content},
    modified_date = #{modifiedDate}
WHERE id = #{id};
